$(window).scroll(function (event) {
	"use strict";
	$(".full-stack").each(function (i) {
		var top = $(this).offset().top - $(window).scrollTop();

		if (top <= $(this).height() * i)
			$(this).css("top", $(this).height() * i)
			.css("position", "fixed");

		if ($(window).scrollTop() + ($(this).height() * i) <= $(this).data("top")) {
			$(this).css("top", $(this).data("top"))
				.css("position", "");
		}
	});
});

function generateFullStack(e) {
	"use strict";
	var letters = "HI BIG DICK".split('');

	$.each(letters, function (key, value) {
		var item = $("<span class=\"full-stack\">" + value + "</span>")
			.css("height", $(window).height() / letters.length)
			.css("top", ($(document).height() / letters.length) * key)
			.attr("data-top", ($(document).height() / letters.length) * key)
			.appendTo("#resume-container");
	});
}


$(document).on("click", "#btnSubmitMesssage", function () {
	var triggerbutton = $(this);
	var first_name = $("#first_name").val();
	var last_name = $("#last_name").val();
	var email = $("#email").val();
	var telephone = $("#telephone").val();
	var comments = $("#comments").val();
	triggerbutton.prop("disabled", true);
	triggerbutton.html('<i class="fas fa-circle-notch fa-spin"></i> Warten');
	$.ajax({
		type: "post",
		url: "send_form_email.php",
		data: {
			'first_name': first_name,
			'last_name': last_name,
			'email': email,
			'telephone': telephone,
			'comments': comments
		},
		cache: false,
		success: function (html) {
			alert(html);
			$('#contactform').find('input:text, input:password, select, textarea').val('');
			triggerbutton.prop("disabled", false);
			triggerbutton.html('<i class="fas fa-paper-plane"></i> Sended');
		}
	});
	return false;
});
